import React, { useState } from 'react';

function CurrencyConvertor() {
  const [inr, setInr] = useState('');
  const [eur, setEur] = useState(0);

  // This is the event handler for the button click
  const handleSubmit = () => {
    // Conversion rate (example rate, can be updated)
    const conversionRate = 0.011; // 1 INR = 0.011 EUR
    const result = parseFloat(inr) * conversionRate;

    // Update the state with the calculated value, formatted to 2 decimal places
    if (!isNaN(result)) {
        setEur(result.toFixed(2));
    } else {
        setEur(0);
    }
  };

  return (
    <div>
      <h2>Currency Converter</h2>
      <h4>Convert INR to EURO</h4>
      <input
        type="number"
        placeholder="Enter INR"
        value={inr}
        onChange={(e) => setInr(e.target.value)}
      />
      {/* The button's click event invokes the handleSubmit handler */}
      <button onClick={handleSubmit}>Convert</button>
      <h3>{eur} EUR</h3>
    </div>
  );
}

export default CurrencyConvertor;
